    public String getStatusImc(double imc) {
        if (imc < 18.5) {
            return "Abaixo do peso";
        } else if (imc <= 24.99) {
            return "Peso normal";
        } else if (imc <= 29.99) {
            return "Acima do peso";
        } else {
            return "Obeso";
        }
    }

